
class BaseSanicException(Exception):
    pass


class TaskExecutorException(BaseSanicException):
    pass


class HTTPInterServiceRequestException(BaseSanicException):
    def __init__(self, error, status_code, meta=None):
        self._error = error
        self._status_code = status_code
        self._meta = meta

    @property
    def error(self):
        return self._error

    @property
    def status_code(self):
        return self._status_code

    @property
    def meta(self):
        return self._meta


class HTTPRequestException(Exception):
    def __init__(self, error):
        self._error = error

    @property
    def error(self):
        return self._error


class HTTPRequestTimeoutException(Exception):
    def __init__(self, error):
        self._error = error

    @property
    def error(self):
        return self._error


class BadRequestException(BaseSanicException):
    def __init__(self, error):
        self._error = error

    @property
    def error(self):
        return self._error


class JsonDecodeException(BaseSanicException):
    def __init__(self, error):
        self._error = error

    @property
    def error(self):
        return self._error
